import React, {useEffect, useState} from 'react';
import { StyleSheet, Text, View, Button, ActivityIndicator } from 'react-native';
import { StatusBar } from 'expo-status-bar';


export default function App() {
  let [isLoading, setIsLoading] = useState(true);
  let [error, setError] = useState();
  let [response, setResponse] = useState();

  useEffect(() => {
    fetch("https://apis.is/petrol")
        .then(response => {
          return response.json();
        })
        .then(result => {
          setResponse(result.results)
          setIsLoading(false);
        })

    // fetch("https://apis.is/petrol")
    //     .then(response => response.json())
    //     .then(
    //         (result) => {
    //           setIsLoading(false);
    //           setResponse(result);
    //         },
    //         (error) => {
    //           setIsLoading(false);
    //           setError(error);
    //         }
    //     )
        console.log("asd")
  }, []);

  const getContent = () => {
    if (isLoading) {
      return <ActivityIndicator size="large" />;
    }

    if (error) {
      return <Text>{error}</Text>
    }

    console.log("GetContent");
    console.log(response);

    let data = response;

    let geoLat = 63.996515;
    let geoLong = -22.560588;

    function deg2rad(deg) {
      return deg * (Math.PI/180)
    }

    function dist(lat1, lon1, lat2, lon2) {
      var R = 6371; // Radius of the earth in km
      var dLat = deg2rad(lat2-lat1);  // deg2rad below
      var dLon = deg2rad(lon2-lon1); 
      var a = 
        Math.sin(dLat/2) * Math.sin(dLat/2) +
        Math.cos(deg2rad(lat1)) * Math.cos(deg2rad(lat2)) * 
        Math.sin(dLon/2) * Math.sin(dLon/2)
        ; 
      var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a)); 
      var d = R * c; // Distance in km
      return d;
    }

    for(let i = 0; i < data.length; i++){
      data[i]["distance"] = dist(geoLat, geoLong, data[i]["geo"]["lat"], data[i]["geo"]["lon"])
    }

    data.sort((a,b) => a.distance > b.distance)






    return (
        <View>

        </View>
    );
  };

  return (
    <View style={styles.container}>
      {getContent()}
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  h3: {
    fontWeight: "bolder"
  },
  info: {
    display: "flex",
    justifyContent: "space-between",

    alignItems: "center",
    paddingLeft: "5px",
    paddingRight: "50px",

  },
  container: {
    flex: '1',
    backgroundColor: '#fff',
    color: "#FFFFFF",
    alignItems: 'center',
    justifyContent: 'center',
  },
  station: {
    display: "flex",

    backgroundColor: '#BCE3FF',
    margin: '10px',
    marginBottom: '5px',
    padding: '20px',
    borderRadius: "20px"
  },
  button: {
    textDecorationLine: "none",
    padding: "20px",
    borderRadius: "5px",
    backgroundColor: "#789EFF",
    color: "#ffffff",
    fontWeight: "900"
  }

});
